import { Component, OnInit, OnDestroy } from '@angular/core';
import { Subscription } from 'rxjs';
import { Task } from '../../models/task.model';
import { TaskService } from '../../services/task.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-task-list',
  templateUrl: './task-list.component.html',
  styleUrls: ['./task-list.component.scss']
})
export class TaskListComponent implements OnInit, OnDestroy {
  tasks: Task[] = [];
  subscription!: Subscription;

  // UI state
  searchTerm = '';
  page = 1;
  pageSize = 20;
  showFormModal = false;
  selectedTask: Task | null = null;
  showDeleteConfirm = false;
  taskToDelete: Task | null = null;

  constructor(public taskService: TaskService, private router: Router) {}

  ngOnInit(): void {
    this.subscription = this.taskService.getAll().subscribe(list => {
      this.tasks = list;
      if (this.page > this.totalPages) this.page = this.totalPages || 1;
    });
  }

  ngOnDestroy(): void {
    this.subscription?.unsubscribe();
  }

  openNew() {
    this.selectedTask = null;
    this.showFormModal = true;
  }

  openEdit(task: Task) {
    this.selectedTask = task;
    this.showFormModal = true;
  }

  onSaved(task: Task) {
    this.showFormModal = false;
    this.selectedTask = null;
  }

  onClosed() {
    this.showFormModal = false;
    this.selectedTask = null;
  }

  openDeleteConfirm(task: Task) {
    this.selectedTask = task;
    this.showDeleteConfirm = true;
  }

  confirmDelete(yes: boolean) {
    if (yes && this.selectedTask) {
      this.taskService.delete(this.selectedTask.id).subscribe();
    }
    this.showDeleteConfirm = false;
    this.selectedTask = null;
  }

  toggleComplete(task: Task) {
    this.taskService.toggleComplete(task.id).subscribe();
  }

  get filteredTasks(): Task[] {
    const q = this.searchTerm.trim().toLowerCase();
    let list = this.tasks;
    if (q) {
      list = list.filter(t =>
        (t.title || '').toLowerCase().includes(q) ||
        (t.assignedTo || '').toLowerCase().includes(q) ||
        (t.comments || '').toLowerCase().includes(q)
      );
    }
    return list;
  }

  get totalPages(): number {
    return Math.max(1, Math.ceil(this.filteredTasks.length / this.pageSize));
  }

  get pagedTasks(): Task[] {
    const start = (this.page - 1) * this.pageSize;
    return this.filteredTasks.slice(start, start + this.pageSize);
  }

  goToPage(p: number) {
    if (p < 1) p = 1;
    if (p > this.totalPages) p = this.totalPages;
    this.page = p;
  }
}
<div class="slds-scope">
  <article class="slds-card">
    <div class="slds-card__header slds-grid">
      <header class="slds-media slds-media_center slds-has-flexi-truncate">
        <div class="slds-media__figure">
          <span class="slds-icon_container slds-icon-standard-task" title="tasks">
            <svg class="slds-icon slds-icon_small" aria-hidden="true">
              <use xlink:href="assets/slds/icons/standard-sprite/svg/symbols.svg#task"></use>
            </svg>
          </span>
        </div>
        <div class="slds-media__body">
          <h2 class="slds-card__header-title">
            <a href="javascript:void(0);" class="slds-card__header-link slds-truncate">
              <span class="slds-text-heading_small">Tasks</span>
            </a>
          </h2>
          <p class="slds-text-body_small">All Tasks</p>
        </div>
      </header>
      <div class="slds-no-flex">
        <button class="slds-button slds-button_neutral" (click)="openNew()">
          <svg class="slds-button__icon slds-button__icon_left" aria-hidden="true">
            <use xlink:href="assets/slds/icons/utility-sprite/svg/symbols.svg#add"></use>
          </svg>
          New Task
        </button>
        <button class="slds-button slds-button_neutral" (click)="taskService.getAll().subscribe()">
          <svg class="slds-button__icon slds-button__icon_left" aria-hidden="true">
            <use xlink:href="assets/slds/icons/utility-sprite/svg/symbols.svg#refresh"></use>
          </svg>
          Refresh
        </button>
      </div>
    </div>

  <div class="slds-card__body slds-card__body_inner">
    <div class="slds-grid slds-grid_align-end slds-m-bottom_small">
      <div class="slds-col">
        <div class="slds-form-element slds-form-element_compound">
          <div class="slds-form-element__control slds-input-has-icon slds-input-has-icon_right">
            <input type="text" class="slds-input" [(ngModel)]="searchTerm" placeholder="Search" />
            <span class="slds-icon_container slds-icon-utility-search slds-input__icon slds-input__icon_right" title="Search">
              <svg class="slds-icon slds-icon_x-small" aria-hidden="true"><use xlink:href="assets/slds/icons/utility-sprite/svg/symbols.svg#search"></use></svg>
            </span>
          </div>
        </div>
      </div>
    </div>

    <div class="slds-card__body">
      <div class="slds-grid slds-gutters slds-p-horizontal_medium slds-p-vertical_x-small">
        <div class="slds-col">
          <div class="slds-form-element">
            <div class="slds-form-element__control slds-input-has-icon slds-input-has-icon_right">
              <input type="search" class="slds-input" placeholder="Search tasks..." [(ngModel)]="searchTerm" />
              <svg class="slds-icon slds-input__icon slds-input__icon_right slds-icon-text-default" aria-hidden="true">
                <use xlink:href="assets/slds/icons/utility-sprite/svg/symbols.svg#search"></use>
              </svg>
            </div>
          </div>
        </div>
        <div class="slds-col slds-col_bump-left">
          <div class="slds-form-element">
            <div class="slds-form-element__control">
              <div class="slds-select_container">
                <select class="slds-select" [(ngModel)]="pageSize">
                  <option value="5">5</option>
                  <option value="10">10</option>
                  <option value="20">20</option>
                  <option value="50">50</option>
                </select>
              </div>
            </div>
          </div>
        </div>
      </div>

      <table class="slds-table slds-table_bordered slds-table_cell-buffer slds-table_striped">
        <thead>
          <tr class="slds-line-height_reset">
            <th class="slds-cell-shrink" scope="col">
              <div class="slds-form-element">
                <div class="slds-form-element__control">
                  <input type="checkbox" class="slds-checkbox" disabled />
                </div>
              </div>
            </th>
            <th scope="col"><div class="slds-truncate" title="Assigned To">Assigned To</div></th>
            <th scope="col"><div class="slds-truncate" title="Status">Status</div></th>
            <th scope="col"><div class="slds-truncate" title="Due Date">Due Date</div></th>
            <th scope="col"><div class="slds-truncate" title="Priority">Priority</div></th>
            <th scope="col"><div class="slds-truncate" title="Comments">Comments</div></th>
            <th scope="col"><div class="slds-truncate" title="Actions">Actions</div></th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let task of pagedTasks" class="slds-hint-parent">
            <td><input type="checkbox" [checked]="task.completed" (change)="toggleComplete(task)"></td>
            <td>{{ task.assignedTo || '-' }}</td>
            <td>{{ task.status || '-' }}</td>
            <td>{{ task.dueDate ? (task.dueDate | date:'shortDate') : '-' }}</td>
            <td>{{ task.priority || '-' }}</td>
            <td>{{ task.comments || task.description || '-' }}</td>
            <td>
              <div class="slds-button-group" role="group">
                <button class="slds-button slds-button_icon-border-filled" title="Edit" (click)="openEdit(task)">
                  <svg class="slds-button__icon" aria-hidden="true"><use xlink:href="assets/icons/utility-sprite/svg/symbols.svg#edit"></use></svg>
                </button>
                <button class="slds-button slds-button_icon-border-filled" title="Delete" (click)="openDeleteConfirm(task)">
                  <svg class="slds-button__icon" aria-hidden="true"><use xlink:href="assets/icons/utility-sprite/svg/symbols.svg#delete"></use></svg>
                </button>
              </div>
            </td>
          </tr>
          <tr *ngIf="pagedTasks.length === 0">
            <td colspan="7" class="slds-text-align_center slds-p-around_medium">No tasks found.</td>
          </tr>
        </tbody>
      </table>
    </div>

    <div class="slds-grid slds-grid_align-spread slds-m-top_medium">
      <div>
        <label class="slds-form-element__control">Show
          <select class="slds-select" [(ngModel)]="pageSize">
            <option [value]="10">10</option>
            <option [value]="20">20</option>
            <option [value]="50">50</option>
          </select>
        </label>
      </div>
      <div>
        <div class="slds-button-group" role="group">
          <button class="slds-button slds-button_neutral" (click)="goToPage(1)">First</button>
          <button class="slds-button slds-button_neutral" (click)="goToPage(page-1)">Prev</button>
          <span class="slds-m-horizontal_small">{{ page }} / {{ totalPages }}</span>
          <button class="slds-button slds-button_neutral" (click)="goToPage(page+1)">Next</button>
          <button class="slds-button slds-button_neutral" (click)="goToPage(totalPages)">Last</button>
        </div>
      </div>
    </div>
  </div>

  <footer class="slds-card__footer slds-grid slds-grid_align-end">
    <div class="slds-text-body_small">{{ filteredTasks.length }} records</div>
  </footer>

  <app-task-form *ngIf="showFormModal" [task]="selectedTask" (saved)="onSaved($event)" (closed)="onClosed()"></app-task-form>

  <section role="dialog" class="slds-modal slds-fade-in-open" *ngIf="showDeleteConfirm">
    <div class="slds-modal__container">
      <header class="slds-modal__header slds-modal__header_empty">
        <h2 class="slds-text-heading_medium slds-align_absolute-center">Delete</h2>
      </header>
      <div class="slds-modal__content slds-p-around_medium">
        Do you want to delete task "{{ selectedTask?.title }}"?
      </div>
      <footer class="slds-modal__footer slds-grid slds-grid_align-end">
        <button class="slds-button slds-button_neutral" (click)="confirmDelete(false)">No</button>
        <button class="slds-button slds-button_destructive" (click)="confirmDelete(true)">Yes</button>
      </footer>
    </div>
    <div class="slds-backdrop slds-backdrop_open"></div>
  </section>
</article>
</div>
