import { Component, OnInit, Input, Output, EventEmitter, OnChanges, SimpleChanges } from '@angular/core';
import { Task } from '../../models/task.model';
import { TaskService } from '../../services/task.service';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-task-form',
  templateUrl: './task-form.component.html',
  styleUrls: ['./task-form.component.scss']
})
export class TaskFormComponent implements OnInit, OnChanges {
  @Input() task?: Task | null = null; // if provided, form is edit
  @Output() saved = new EventEmitter<Task>();
  @Output() closed = new EventEmitter<void>();

  taskForm!: FormGroup;
  users: string[] = [];
  statuses: string[] = [];
  priorities: string[] = [];
  
  get isEdit(): boolean {
    return !!this.task;
  }

  constructor(
    private fb: FormBuilder,
    private taskService: TaskService
  ) {}

  ngOnInit(): void {
    this.taskForm = this.fb.group({
      // Title is optional for this app - keep max length but not required
      title: ['', [Validators.maxLength(120)]],
      description: [''],
      dueDate: [''],
      assignedTo: [''],
      status: ['Not Started', Validators.required],
      priority: ['Normal', Validators.required],
      comments: [''],
      completed: [false]
    });

    // load selects
    this.taskService.getUsers().subscribe(list => this.users = list);
    this.taskService.getStatuses().subscribe(list => this.statuses = list);
    this.taskService.getPriorities().subscribe(list => this.priorities = list);

    if (this.task) {
      this.patchForm(this.task);
    }
  }

  ngOnChanges(changes: SimpleChanges) {
    if (changes['task'] && this.taskForm) {
      if (this.task) this.patchForm(this.task);
      else this.taskForm.reset({ status: 'Not Started', priority: 'Normal', completed: false });
    }
  }

  private patchForm(t: Task) {
    this.taskForm.patchValue({
      title: t.title,
      description: t.description,
      dueDate: t.dueDate ? t.dueDate.split('T')[0] : '',
      assignedTo: t.assignedTo || '',
      status: t.status || 'Not Started',
      priority: t.priority || 'Normal',
      comments: t.comments || '',
      completed: !!t.completed
    });
  }

  onSubmit() {
    if (this.taskForm.invalid) {
      this.taskForm.markAllAsTouched();
      return;
    }

    const f = this.taskForm.value;
    const payload: Partial<Task> = {
      title: f.title,
      description: f.description,
      dueDate: f.dueDate ? new Date(f.dueDate).toISOString() : undefined,
      assignedTo: f.assignedTo,
      status: f.status,
      priority: f.priority,
      comments: f.comments,
      completed: !!f.completed
    };

    if (this.task && this.task.id) {
      this.taskService.update(this.task.id, payload).subscribe(updated => {
        if (updated) this.saved.emit(updated);
      });
    } else {
      this.taskService.create(payload).subscribe(created => this.saved.emit(created));
    }
  }

  onCancel() {
    this.closed.emit();
  }
}
<div class="slds-backdrop slds-backdrop_open"></div>
<div role="dialog" class="slds-modal slds-fade-in-open">
  <div class="slds-modal__container">
    <header class="slds-modal__header">
      <h2 class="slds-text-heading_medium slds-modal__title">{{ isEdit ? 'Edit Task' : 'New Task' }}</h2>
      <button type="button" class="slds-button slds-button_icon slds-modal__close" aria-label="Close" (click)="onCancel()">
        <svg class="slds-button__icon slds-button__icon_large" aria-hidden="true">
          <use xlink:href="assets/slds/icons/utility-sprite/svg/symbols.svg#close"></use>
        </svg>
        <span class="slds-assistive-text">Close</span>
      </button>
    </header>
    <div class="slds-modal__content slds-p-around_medium">
      <form [formGroup]="taskForm" (ngSubmit)="onSubmit()" class="slds-form_stacked">
        <div class="slds-grid slds-gutters">
          <div class="slds-col slds-size_1-of-1 slds-medium-size_1-of-2">
            <div class="slds-form-element">
              <label class="slds-form-element__label" for="assignedTo">Assigned To</label>
              <div class="slds-form-element__control">
                <div class="slds-select_container">
                  <select id="assignedTo" class="slds-select" formControlName="assignedTo">
                    <option value="">Select user</option>
                    <option *ngFor="let u of users" [value]="u">{{ u }}</option>
                  </select>
                </div>
              </div>
            </div>
          </div>

          <div class="slds-col slds-size_1-of-1 slds-medium-size_1-of-2">
            <div class="slds-form-element">
              <label class="slds-form-element__label" for="status">Status</label>
              <div class="slds-form-element__control">
                <div class="slds-select_container">
                  <select id="status" class="slds-select" formControlName="status">
                    <option *ngFor="let s of statuses" [value]="s">{{ s }}</option>
                  </select>
                </div>
              </div>
            </div>
          </div>
        </div>

        <div class="slds-grid slds-gutters slds-m-top_small">
          <div class="slds-col slds-size_1-of-1 slds-medium-size_1-of-2">
            <div class="slds-form-element">
              <label class="slds-form-element__label" for="dueDate">Due Date</label>
              <div class="slds-form-element__control slds-input-has-icon slds-input-has-icon_right">
                <input type="date" id="dueDate" formControlName="dueDate" class="slds-input">
                <span class="slds-input__icon slds-input__icon_right" title="Select date">
                  <svg class="slds-icon slds-icon_x-small" aria-hidden="true">
                    <use xlink:href="assets/slds/icons/utility-sprite/svg/symbols.svg#event"></use>
                  </svg>
                </span>
              </div>
            </div>
          </div>

          <div class="slds-col slds-size_1-of-1 slds-medium-size_1-of-2">
            <div class="slds-form-element">
              <label class="slds-form-element__label" for="priority">Priority</label>
              <div class="slds-form-element__control">
                <div class="slds-select_container">
                  <select id="priority" class="slds-select" formControlName="priority">
                    <option *ngFor="let p of priorities" [value]="p">{{ p }}</option>
                  </select>
                </div>
              </div>
            </div>
          </div>
        </div>

        <div class="slds-form-element slds-m-top_small">
          <label class="slds-form-element__label" for="description">Description</label>
          <div class="slds-form-element__control">
            <textarea id="description" formControlName="description" class="slds-textarea" rows="4" placeholder="Enter task description"></textarea>
          </div>
        </div>

        <!-- Completed checkbox removed per design; closing handled by header close button -->
      </form>
    </div>
    <footer class="slds-modal__footer">
      <button type="button" class="slds-button custom-yellow-button" (click)="onCancel()">Cancel</button>
      <button type="submit" class="slds-button slds-button_brand" (click)="onSubmit()">{{ isEdit ? 'Update' : 'Save' }}</button>
    </footer>
  </div>
</div>
