import { Component, OnInit, OnDestroy } from '@angular/core';
import { Subscription } from 'rxjs';
import { Task } from '../../models/task.model';
import { TaskService } from '../../services/task.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-task-list',
  templateUrl: './task-list.component.html',
  styleUrls: ['./task-list.component.scss']
})
export class TaskListComponent implements OnInit, OnDestroy {
  tasks: Task[] = [];
  subscription!: Subscription;

  // UI state
  searchTerm = '';
  page = 1;
  pageSize = 20;
  showFormModal = false;
  selectedTask: Task | null = null;
  showDeleteConfirm = false;
  taskToDelete: Task | null = null;
  // track which row's action menu is open (task id)
  openMenuTaskId: string | null = null;
  private clickListener: (() => void) | null = null;

  constructor(public taskService: TaskService, private router: Router) {}

  ngOnInit(): void {
    this.subscription = this.taskService.getAll().subscribe(list => {
      this.tasks = list;
      if (this.page > this.totalPages) this.page = this.totalPages || 1;
    });
  }

  ngOnDestroy(): void {
    this.subscription?.unsubscribe();
    this.removeClickListener();
  }

  openNew() {
    this.selectedTask = null;
    this.showFormModal = true;
  }

  openEdit(task: Task) {
    this.selectedTask = task;
    this.showFormModal = true;
    this.openMenuTaskId = null;
  }

  onSaved(task: Task) {
    this.showFormModal = false;
    this.selectedTask = null;
  }

  onClosed() {
    this.showFormModal = false;
    this.selectedTask = null;
  }

  openDeleteConfirm(task: Task) {
    this.selectedTask = task;
    this.showDeleteConfirm = true;
    this.openMenuTaskId = null;
  }

  toggleRowMenu(id: string) {
    if (this.openMenuTaskId === id) {
      this.openMenuTaskId = null;
      this.removeClickListener();
    } else {
      this.openMenuTaskId = id;
      setTimeout(() => this.addClickListener(), 0);
    }
  }

  closeRowMenu(event: Event) {
    event.preventDefault();
    this.openMenuTaskId = null;
    this.removeClickListener();
  }
  private addClickListener() {
    if (this.clickListener) return;
    const handler = (event: MouseEvent) => {
      const menu = document.querySelector('.slds-dropdown-trigger.slds-is-open');
      if (!menu) return;
      const target = event.target as HTMLElement;
      if (!menu.contains(target)) {
        this.openMenuTaskId = null;
        this.removeClickListener();
      }
    };
    document.addEventListener('mousedown', handler);
    this.clickListener = () => document.removeEventListener('mousedown', handler);
  }

  private removeClickListener() {
    if (this.clickListener) {
      this.clickListener();
      this.clickListener = null;
    }
  }

  confirmDelete(yes: boolean) {
    if (yes && this.selectedTask) {
      this.taskService.delete(this.selectedTask.id).subscribe();
    }
    this.showDeleteConfirm = false;
    this.selectedTask = null;
  }

  toggleComplete(task: Task) {
    this.taskService.toggleComplete(task.id).subscribe();
  }

  get filteredTasks(): Task[] {
    const q = this.searchTerm.trim().toLowerCase();
    let list = this.tasks;
    if (q) {
      list = list.filter(t =>
        (t.title || '').toLowerCase().includes(q) ||
        (t.assignedTo || '').toLowerCase().includes(q) ||
        (t.comments || '').toLowerCase().includes(q)
      );
    }
    return list;
  }

  get totalPages(): number {
    return Math.max(1, Math.ceil(this.filteredTasks.length / this.pageSize));
  }

  get pagedTasks(): Task[] {
    const start = (this.page - 1) * this.pageSize;
    return this.filteredTasks.slice(start, start + this.pageSize);
  }

  goToPage(p: number) {
    if (p < 1) p = 1;
    if (p > this.totalPages) p = this.totalPages;
    this.page = p;
  }
}
<div class="slds-scope">
  <article class="slds-card">
    <div class="slds-card__header slds-grid">
      <header class="slds-media slds-media_center slds-has-flexi-truncate">
        <div class="slds-media__figure">
          <span
            class="slds-icon_container slds-icon-standard-task"
            title="tasks"
          >
            <svg class="slds-icon slds-icon_small" aria-hidden="true">
              <use
                xlink:href="assets/slds/icons/standard-sprite/svg/symbols.svg#task"
              ></use>
            </svg>
          </span>
        </div>
        <div class="slds-media__body">
          <h2 class="slds-card__header-title">
            <a
              href="javascript:void(0);"
              class="slds-card__header-link slds-truncate"
            >
              <span class="slds-text-heading_small">Tasks</span>
            </a>
          </h2>
          <p class="slds-text-body_small">All Tasks</p>
          <p class="slds-text-body_small slds-m-top_xx-small">{{ filteredTasks.length }} records</p>
        </div>
      </header>
      <div class="slds-grid slds-grid_align-end">
        <div class="slds-grid slds-wrap" style="width: 300px">
          <div class="slds-col slds-size_1-of-1 slds-m-bottom_x-small">
            <button class="slds-button custom-yellow-button slds-p-horizontal_medium slds-p-vertical_xx-small slds-m-right_x-small" (click)="openNew()">
              <span class="slds-text-align_center">New Task</span>
            </button>
            <button
              class="slds-button custom-yellow-button slds-p-horizontal_medium slds-p-vertical_xx-small"
              (click)="taskService.getAll().subscribe()"
            >
              <span class="slds-text-align_center">Refresh</span>
            </button>
          </div>
          <div class="slds-col slds-size_1-of-1">
            <div class="slds-form-element">
              <div class="slds-form-element__control slds-input-has-icon slds-input-has-icon_right">
                <input
                  type="search"
                  class="slds-input"
                  placeholder="Search tasks..."
                  [(ngModel)]="searchTerm"
                />
                <svg class="slds-icon slds-input__icon slds-input__icon_right slds-icon-text-default" aria-hidden="true">
                  <use xlink:href="assets/slds/icons/utility-sprite/svg/symbols.svg#search"></use>
                </svg>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>

    <div class="slds-card__body">
      <div class="slds-card__body_inner">
        <div class="slds-p-horizontal_medium slds-p-vertical_x-small slds-m-bottom_small">
        </div>        <table
          class="slds-table slds-table_bordered slds-table_fixed-layout slds-table_striped slds-no-row-hover"
        >
          <thead>
            <tr class="slds-line-height_reset">
              <th class="slds-cell-shrink" scope="col">
                <div class="slds-th__action">
                  <div class="slds-form-element">
                    <div class="slds-form-element__control">
                      <input type="checkbox" class="slds-checkbox" disabled />
                    </div>
                  </div>
                </div>
              </th>
              <th scope="col">
                <div class="slds-th__action slds-text-link_reset">
                  <span class="slds-truncate" title="Assigned To"
                    >Assigned To</span
                  >
                </div>
              </th>
              <th scope="col">
                <div class="slds-th__action slds-text-link_reset">
                  <span class="slds-truncate" title="Status">Status</span>
                </div>
              </th>
              <th scope="col">
                <div class="slds-th__action slds-text-link_reset">
                  <span class="slds-truncate" title="Due Date">Due Date</span>
                </div>
              </th>
              <th scope="col">
                <div class="slds-th__action slds-text-link_reset">
                  <span class="slds-truncate" title="Priority">Priority</span>
                </div>
              </th>
              <th scope="col">
                <div class="slds-th__action slds-text-link_reset">
                  <span class="slds-truncate" title="Comments">Comments</span>
                </div>
              </th>
              <th scope="col" style="width: 6rem">
                <!-- Actions column header intentionally empty (icon-only per row) -->
                <div class="slds-th__action">
                  <span class="slds-assistive-text">Row actions</span>
                </div>
              </th>
            </tr>
          </thead>
          <tbody>
            <tr *ngFor="let task of pagedTasks" class="slds-hint-parent">
              <td class="slds-cell-shrink">
                <div class="slds-form-element">
                  <div class="slds-form-element__control">
                    <span class="slds-checkbox">
                      <input type="checkbox" [checked]="task.completed" (change)="toggleComplete(task)" />
                      <span class="slds-checkbox_faux"></span>
                    </span>
                  </div>
                </div>
              </td>

              <td><div class="slds-truncate">{{ task.assignedTo || '-' }}</div></td>

              <td>
                <div class="slds-badge" [ngClass]="{
                  'slds-theme_success': task.status === 'Completed',
                  'slds-theme_warning': task.status === 'In Progress',
                  'slds-theme_error': task.status === 'Blocked',
                  'slds-theme_info': !task.status
                }">{{ task.status || 'Not Started' }}</div>
              </td>

              <td><div class="slds-truncate">{{ task.dueDate ? (task.dueDate | date:'shortDate') : '-' }}</div></td>

              <td>
                <div class="slds-badge" [ngClass]="{
                  'slds-theme_error': task.priority === 'High',
                  'slds-theme_warning': task.priority === 'Medium',
                  'slds-theme_success': task.priority === 'Low',
                  'slds-theme_info': !task.priority
                }">{{ task.priority || 'None' }}</div>
              </td>

              <td>
                <div class="slds-truncate" [title]="task.comments || task.description || '-'">{{ task.comments || task.description || '-' }}</div>
              </td>

              <td style="position:relative;">
                <div class="slds-dropdown-trigger slds-dropdown-trigger_click" [class.slds-is-open]="openMenuTaskId === task.id" style="z-index:99999;">
                  <button
  class="slds-button slds-button_icon slds-button_icon-border-filled slds-button_icon-x-small"
  aria-haspopup="true"
  (click)="toggleRowMenu(task.id)"
  title="More actions"
>
  <svg class="slds-button__icon slds-button__icon_xx-small" aria-hidden="true">
    <use xlink:href="assets/slds/icons/utility-sprite/svg/symbols.svg#down"></use>
  </svg>
  <span class="slds-assistive-text">Open actions</span>
</button>
                  <div class="slds-dropdown slds-dropdown_right" *ngIf="openMenuTaskId === task.id" style="position:absolute; right:2rem; top:rem; z-index:99999; min-width:8rem;">
                    <ul class="slds-dropdown__list" role="menu">
                      <li class="slds-dropdown__item" role="presentation">
                        <a role="menuitem" href="#" (click)="openEdit(task); closeRowMenu($event)">Edit</a>
                      </li>
                      <li class="slds-dropdown__item" role="presentation">
                        <a role="menuitem" href="#" (click)="openDeleteConfirm(task); closeRowMenu($event)">Delete</a>
                      </li>
                    </ul>
                  </div>
                </div>
              </td>
            </tr>
            <tr *ngIf="pagedTasks.length === 0">
              <td
                colspan="7"
                class="slds-text-align_center slds-p-around_medium"
              >
                No tasks found.
              </td>
            </tr>
          </tbody>
        </table>
      </div>

    </div>
    <footer class="slds-card__footer">
      <div class="slds-grid slds-grid_align-spread">
        <div class="slds-col">
          <div class="slds-form-element">
            <label class="slds-form-element__label" for="page-size-select">Show</label>
            <div class="slds-form-element__control">
              <div class="slds-select_container">
                <select id="page-size-select" class="slds-select" [(ngModel)]="pageSize">
                  <option value="5">5</option>
                  <option value="10">10</option>
                  <option value="20">20</option>
                  <option value="50">50</option>
                </select>
              </div>
            </div>
          </div>
        </div>
        <div class="slds-col">
          <div class="slds-button-group" role="group">
            <button class="slds-button slds-button_neutral" (click)="goToPage(1)">First</button>
            <button class="slds-button slds-button_neutral" (click)="goToPage(page - 1)">Prev</button>
            <span class="slds-m-horizontal_small">{{ page }} / {{ totalPages }}</span>
            <button class="slds-button slds-button_neutral" (click)="goToPage(page + 1)">Next</button>
            <button class="slds-button slds-button_neutral" (click)="goToPage(totalPages)">Last</button>
          </div>
        </div>
      </div>
    </footer>
  </article>
</div>

<!-- Task Form Modal -->
<app-task-form
  *ngIf="showFormModal"
  [task]="selectedTask"
  (saved)="onSaved($event)"
  (closed)="onClosed()"
></app-task-form>

<!-- Delete Confirmation Modal -->
<section
  role="dialog"
  class="slds-modal slds-fade-in-open"
  *ngIf="showDeleteConfirm"
>
  <div class="slds-modal__container">
    <header class="slds-modal__header slds-modal__header_empty">
      <h2 class="slds-text-heading_medium slds-align_absolute-center">
        Delete
      </h2>
    </header>
    <div class="slds-modal__content slds-p-around_medium">
      Do you want to delete task "{{ selectedTask?.title }}"?
    </div>
    <footer class="slds-modal__footer slds-grid slds-grid_align-end">
      <button
        class="slds-button slds-button_neutral"
        (click)="confirmDelete(false)"
      >
        No
      </button>
      <button
        class="slds-button slds-button_destructive"
        (click)="confirmDelete(true)"
      >
        Yes
      </button>
    </footer>
  </div>
  <div class="slds-backdrop slds-backdrop_open"></div>
</section>
