import { Component, OnInit, OnDestroy } from '@angular/core';
import { Subscription } from 'rxjs';
import { Task } from '../../models/task.model';
import { TaskService } from '../../services/task.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-task-list',
  templateUrl: './task-list.component.html',
  styleUrls: ['./task-list.component.scss']
})
export class TaskListComponent implements OnInit, OnDestroy {
  tasks: Task[] = [];
  subscription!: Subscription;

  // UI state
  searchTerm = '';
  page = 1;
  pageSize = 20;
  showFormModal = false;
  selectedTask: Task | null = null;
  showDeleteConfirm = false;

  constructor(private taskService: TaskService, private router: Router) {}

  ngOnInit(): void {
    this.subscription = this.taskService.getAll().subscribe(list => {
      this.tasks = list;
      if (this.page > this.totalPages) this.page = this.totalPages || 1;
    });
  }

  ngOnDestroy(): void {
    this.subscription?.unsubscribe();
  }

  openNew() {
    this.selectedTask = null;
    this.showFormModal = true;
  }

  openEdit(task: Task) {
    this.selectedTask = task;
    this.showFormModal = true;
  }

  onSaved(task: Task) {
    this.showFormModal = false;
    this.selectedTask = null;
  }

  onClosed() {
    this.showFormModal = false;
    this.selectedTask = null;
  }

  openDeleteConfirm(task: Task) {
    this.selectedTask = task;
    this.showDeleteConfirm = true;
  }

  confirmDelete(yes: boolean) {
    if (yes && this.selectedTask) {
      this.taskService.delete(this.selectedTask.id).subscribe();
    }
    this.showDeleteConfirm = false;
    this.selectedTask = null;
  }

  toggleComplete(task: Task) {
    this.taskService.toggleComplete(task.id).subscribe();
  }

  get filteredTasks(): Task[] {
    const q = this.searchTerm.trim().toLowerCase();
    let list = this.tasks;
    if (q) {
      list = list.filter(t =>
        (t.title || '').toLowerCase().includes(q) ||
        (t.assignedTo || '').toLowerCase().includes(q) ||
        (t.comments || '').toLowerCase().includes(q)
      );
    }
    return list;
  }

  get totalPages(): number {
    return Math.max(1, Math.ceil(this.filteredTasks.length / this.pageSize));
  }

  get pagedTasks(): Task[] {
    const start = (this.page - 1) * this.pageSize;
    return this.filteredTasks.slice(start, start + this.pageSize);
  }

  goToPage(p: number) {
    if (p < 1) p = 1;
    if (p > this.totalPages) p = this.totalPages;
    this.page = p;
  }
}
<div class="slds-p-around_medium">
  <div class="slds-grid slds-align-spread slds-m-bottom_medium">
    <h2 class="slds-text-heading_medium">My Tasks</h2>
    <button class="slds-button slds-button_brand" (click)="navigateToCreate()">New Task</button>
  </div>

  <div class="slds-button-group" role="group" aria-label="Filter">
    <button class="slds-button" [class.slds-button_neutral]="filter === 'all'" (click)="filter='all'">All</button>
    <button class="slds-button" [class.slds-button_neutral]="filter === 'active'" (click)="filter='active'">Active</button>
    <button class="slds-button" [class.slds-button_neutral]="filter === 'completed'" (click)="filter='completed'">Completed</button>
  </div>

  <ng-container *ngIf="tasks$ | async as tasks">
    <ul class="slds-list_vertical slds-m-top_medium">
      <li *ngFor="let task of applyFilter(tasks)" class="slds-box slds-m-bottom_small slds-grid slds-grid_align-spread">
        <div class="slds-grid slds-grid_align-spread slds-size_1-of-1">
          <div class="slds-grid slds-align_absolute-center">
            <input type="checkbox" [checked]="task.completed" (change)="onToggle(task)" />
            <div class="slds-m-left_small">
              <div [ngClass]="{'slds-text-title_bold': !task.completed, 'slds-text-color_weak': task.completed}">
                {{ task.title }}
              </div>
              <div class="slds-text-body_small">{{ task.description }}</div>
              <div class="slds-text-body_small slds-m-top_xx-small">
                <small>Due: {{ task.dueDate ? (task.dueDate | date:'shortDate') : '—' }}</small>
              </div>
            </div>
          </div>

          <div class="slds-button-group" role="group">
            <button class="slds-button slds-button_icon slds-button_icon-border-filled" title="Edit" (click)="onEdit(task)">
              <svg class="slds-button__icon" aria-hidden="true"><use xlink:href="#icon-edit"></use></svg>
            </button>
            <button class="slds-button slds-button_icon slds-button_icon-border-filled" title="Delete" (click)="onDelete(task)">
              <svg class="slds-button__icon" aria-hidden="true"><use xlink:href="#icon-delete"></use></svg>
            </button>
          </div>
        </div>
      </li>
    </ul>

    <div *ngIf="applyFilter(tasks).length === 0" class="slds-text-align_center slds-p-around_medium slds-text-color_weak">
      No tasks found.
    </div>
  </ng-container>
</div>
