import { Component, OnInit, OnDestroy } from '@angular/core';
import { Subscription } from 'rxjs';
import { Task } from '../../models/task.model';
import { TaskService } from '../../services/task.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-task-list',
  templateUrl: './task-list.component.html',
  styleUrls: ['./task-list.component.scss']
})
export class TaskListComponent implements OnInit, OnDestroy {
  tasks: Task[] = [];
  subscription!: Subscription;

  // UI state
  searchTerm = '';
  page = 1;
  pageSize = 20;
  showFormModal = false;
  selectedTask: Task | null = null;
  showDeleteConfirm = false;

  constructor(private taskService: TaskService, private router: Router) {}

  ngOnInit(): void {
    this.subscription = this.taskService.getAll().subscribe(list => {
      this.tasks = list;
      if (this.page > this.totalPages) this.page = this.totalPages || 1;
    });
  }

  ngOnDestroy(): void {
    this.subscription?.unsubscribe();
  }

  openNew() {
    this.selectedTask = null;
    this.showFormModal = true;
  }

  openEdit(task: Task) {
    this.selectedTask = task;
    this.showFormModal = true;
  }

  onSaved(task: Task) {
    this.showFormModal = false;
    this.selectedTask = null;
  }

  onClosed() {
    this.showFormModal = false;
    this.selectedTask = null;
  }

  openDeleteConfirm(task: Task) {
    this.selectedTask = task;
    this.showDeleteConfirm = true;
  }

  confirmDelete(yes: boolean) {
    if (yes && this.selectedTask) {
      this.taskService.delete(this.selectedTask.id).subscribe();
    }
    this.showDeleteConfirm = false;
    this.selectedTask = null;
  }

  toggleComplete(task: Task) {
    this.taskService.toggleComplete(task.id).subscribe();
  }

  get filteredTasks(): Task[] {
    const q = this.searchTerm.trim().toLowerCase();
    let list = this.tasks;
    if (q) {
      list = list.filter(t =>
        (t.title || '').toLowerCase().includes(q) ||
        (t.assignedTo || '').toLowerCase().includes(q) ||
        (t.comments || '').toLowerCase().includes(q)
      );
    }
    return list;
  }

  get totalPages(): number {
    return Math.max(1, Math.ceil(this.filteredTasks.length / this.pageSize));
  }

  get pagedTasks(): Task[] {
    const start = (this.page - 1) * this.pageSize;
    return this.filteredTasks.slice(start, start + this.pageSize);
  }

  goToPage(p: number) {
    if (p < 1) p = 1;
    if (p > this.totalPages) p = this.totalPages;
    this.page = p;
  }
}
<div class="task-list-wrapper">
  <div class="header">
    <div class="left">
      <h2>Tasks</h2>
      <small>All Tasks</small>
    </div>
    <div class="right">
      <button class="btn btn-light" (click)="openNew()">New Task</button>
      <button class="btn btn-light" (click)="taskService.getAll().subscribe()">Refresh</button>
      <div class="search">
        <input type="text" [(ngModel)]="searchTerm" placeholder="Search">
        <button class="btn">üîç</button>
      </div>
    </div>
  </div>

  <table class="task-table">
    <thead>
      <tr>
        <th><input type="checkbox" disabled></th>
        <th>Assigned To</th>
        <th>Status</th>
        <th>Due Date</th>
        <th>Priority</th>
        <th>Comments</th>
        <th>Actions</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let task of pagedTasks">
        <td><input type="checkbox" [checked]="task.completed" (change)="toggleComplete(task)"></td>
        <td>{{ task.assignedTo || '-' }}</td>
        <td>{{ task.status || '-' }}</td>
        <td>{{ task.dueDate ? (task.dueDate | date:'shortDate') : '-' }}</td>
        <td>{{ task.priority || '-' }}</td>
        <td>{{ task.comments || task.description || '-' }}</td>
        <td>
          <div class="actions">
            <button class="btn btn-xs" (click)="openEdit(task)">Edit</button>
            <button class="btn btn-xs btn-danger" (click)="openDeleteConfirm(task)">Delete</button>
          </div>
        </td>
      </tr>
      <tr *ngIf="pagedTasks.length === 0">
        <td colspan="7" class="empty">No tasks found.</td>
      </tr>
    </tbody>
  </table>

  <div class="footer">
    <div class="page-controls">
      <button (click)="goToPage(1)">¬´ First</button>
      <button (click)="goToPage(page-1)">‚Äπ Prev</button>
      <span>{{ page }} / {{ totalPages }}</span>
      <button (click)="goToPage(page+1)">Next ‚Ä∫</button>
      <button (click)="goToPage(totalPages)">Last ¬ª</button>
    </div>
  </div>

  <app-task-form *ngIf="showFormModal" [task]="selectedTask" (saved)="onSaved($event)" (closed)="onClosed()"></app-task-form>

  <div class="confirm-modal" *ngIf="showDeleteConfirm">
    <div class="confirm-box">
      <header class="confirm-header">Delete</header>
      <div class="confirm-body">Do you want to delete task "{{ selectedTask?.title }}"?</div>
      <footer class="confirm-footer">
        <button class="btn btn-light" (click)="confirmDelete(false)">No</button>
        <button class="btn btn-danger" (click)="confirmDelete(true)">Yes</button>
      </footer>
    </div>
  </div>
</div>
