import { Component, OnInit, Input, Output, EventEmitter, OnChanges, SimpleChanges } from '@angular/core';
import { Task } from '../../models/task.model';
import { TaskService } from '../../services/task.service';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-task-form',
  templateUrl: './task-form.component.html',
  styleUrls: ['./task-form.component.scss']
})
export class TaskFormComponent implements OnInit, OnChanges {
  @Input() task?: Task | null = null; // if provided, form is edit
  @Output() saved = new EventEmitter<Task>();
  @Output() closed = new EventEmitter<void>();

  taskForm!: FormGroup;
  users: string[] = [];
  statuses: string[] = [];
  priorities: string[] = [];

  constructor(
    private fb: FormBuilder,
    private taskService: TaskService
  ) {}

  ngOnInit(): void {
    this.taskForm = this.fb.group({
      title: ['', [Validators.required, Validators.maxLength(120)]],
      description: [''],
      dueDate: [''],
      assignedTo: [''],
      status: ['Not Started', Validators.required],
      priority: ['Normal', Validators.required],
      comments: [''],
      completed: [false]
    });

    // load selects
    this.taskService.getUsers().subscribe(list => this.users = list);
    this.taskService.getStatuses().subscribe(list => this.statuses = list);
    this.taskService.getPriorities().subscribe(list => this.priorities = list);

    if (this.task) {
      this.patchForm(this.task);
    }
  }

  ngOnChanges(changes: SimpleChanges) {
    if (changes['task'] && this.taskForm) {
      if (this.task) this.patchForm(this.task);
      else this.taskForm.reset({ status: 'Not Started', priority: 'Normal', completed: false });
    }
  }

  private patchForm(t: Task) {
    this.taskForm.patchValue({
      title: t.title,
      description: t.description,
      dueDate: t.dueDate ? t.dueDate.split('T')[0] : '',
      assignedTo: t.assignedTo || '',
      status: t.status || 'Not Started',
      priority: t.priority || 'Normal',
      comments: t.comments || '',
      completed: !!t.completed
    });
  }

  onSubmit() {
    if (this.taskForm.invalid) {
      this.taskForm.markAllAsTouched();
      return;
    }

    const f = this.taskForm.value;
    const payload: Partial<Task> = {
      title: f.title,
      description: f.description,
      dueDate: f.dueDate ? new Date(f.dueDate).toISOString() : undefined,
      assignedTo: f.assignedTo,
      status: f.status,
      priority: f.priority,
      comments: f.comments,
      completed: !!f.completed
    };

    if (this.task && this.task.id) {
      this.taskService.update(this.task.id, payload).subscribe(updated => {
        if (updated) this.saved.emit(updated);
      });
    } else {
      this.taskService.create(payload).subscribe(created => this.saved.emit(created));
    }
  }

  onCancel() {
    this.closed.emit();
  }
}
<div class="slds-p-around_medium slds-size_1-of-1">
  <h2 class="slds-text-heading_medium">{{ isEdit ? 'Edit Task' : 'New Task' }}</h2>

  <form [formGroup]="taskForm" (ngSubmit)="onSubmit()" class="slds-form_stacked slds-m-top_medium">
    <div class="slds-form-element">
      <label class="slds-form-element__label" for="title">Title</label>
      <div class="slds-form-element__control">
          <input type="text" id="title" formControlName="title" placeholder="Enter task title">
      </div>
      <div *ngIf="taskForm.controls.title.touched && taskForm.controls.title.invalid" class="slds-text-color_error slds-m-top_xx-small">
        Title is required and must be less than 120 characters.
      </div>
    </div>

    <div class="slds-form-element">
      <label class="slds-form-element__label" for="description">Description</label>
      <div class="slds-form-element__control">
          <textarea id="description" formControlName="description" placeholder="Enter task description"></textarea>
      </div>
    </div>

    <div class="slds-form-element">
      <label class="slds-form-element__label" for="dueDate">Due Date</label>
      <div class="slds-form-element__control">
          <input type="date" formControlName="dueDate">
      </div>
    </div>

    <div class="slds-form-element slds-m-top_small">
      <label class="slds-checkbox">
        <input type="checkbox" formControlName="completed" />
        <span class="slds-checkbox_faux"></span>
        <span class="slds-form-element__label">Completed</span>
      </label>
    </div>

    <div class="slds-m-top_medium">
      <button type="submit" class="slds-button slds-button_brand">{{ isEdit ? 'Update' : 'Create' }}</button>
      <button type="button" class="slds-button slds-button_neutral" (click)="onCancel()">Cancel</button>
    </div>
      <div class="modal-backdrop">
        <div class="modal">
          <header class="modal-header">
            <h2>{{ task ? 'Edit Task' : 'New Task' }}</h2>
            <button class="close" (click)="onCancel()">✕</button>
          </header>
          <form [formGroup]="taskForm" (ngSubmit)="onSubmit()" class="modal-body">
            <div class="row">
              <div class="col">
                <label>Assigned To</label>
                <select formControlName="assignedTo">
                  <option value="">-- Select --</option>
                  <option *ngFor="let u of users" [value]="u">{{ u }}</option>
                </select>
              </div>
              <div class="col">
                <label>Status</label>
                <select formControlName="status">
                  <option *ngFor="let s of statuses" [value]="s">{{ s }}</option>
                </select>
              </div>
            </div>

            <div class="row">
              <div class="col">
                <label>Due Date</label>
                <input type="date" formControlName="dueDate">
              </div>
              <div class="col">
                <label>Priority</label>
                <select formControlName="priority">
                  <option *ngFor="let p of priorities" [value]="p">{{ p }}</option>
                </select>
              </div>
            </div>

            <div class="row single">
              <label>Description</label>
              <textarea formControlName="description"></textarea>
            </div>

            <footer class="modal-footer">
              <button type="button" class="btn btn-cancel" (click)="onCancel()">Cancel</button>
              <button type="submit" class="btn btn-save" [disabled]="taskForm.invalid">Save</button>
            </footer>
          </form>
        </div>
      </div>
  </form>
</div>
